function parse(e){var t=/^((?:\d+)?\.?\d+) *(ms|seconds?|s|minutes?|m|hours?|h|days?|d|years?|y)?$/i.exec(e);if(!t)return;var n=parseFloat(t[1]),r=(t[2]||"ms").toLowerCase();switch(r){case"years":case"year":case"y":return n*315576e5;case"days":case"day":case"d":return n*864e5;case"hours":case"hour":case"h":return n*36e5;case"minutes":case"minute":case"m":return n*6e4;case"seconds":case"second":case"s":return n*1e3;case"ms":return n}}function format(e){return e==d?Math.round(e/d)+" day":e>d?Math.round(e/d)+" days":e==h?Math.round(e/h)+" hour":e>h?Math.round(e/h)+" hours":e==m?Math.round(e/m)+" minute":e>m?Math.round(e/m)+" minutes":e==s?Math.round(e/s)+" second":e>s?Math.round(e/s)+" seconds":e+" ms"}var s=1e3,m=s*60,h=m*60,d=h*24;module.exports=function(e){return"string"==typeof e?parse(e):format(e)};