describe("Array",function(){describe("#push()",function(){it("should append a value",function(){foo="asdf";var e=[];e.push("foo"),e.push("bar"),e.push("baz"),assert("foo"==e[0]),assert("bar"==e[1]),assert("baz"==e[2])}),it("should return the length",function(){var e=[];assert(1==e.push("foo")),assert(2==e.push("bar")),assert(3==e.push("baz"))})})}),describe("Array",function(){describe("#pop()",function(){it("should remove and return the last value",function(){var e=[1,2,3];assert(e.pop()==3),assert(e.pop()==2),assert(e.pop()==-1)}),it("should adjust .length",function(){var e=[1,2,3];e.pop(),assert(e.length==2)})})});