var n=30;while(n--)describe("Array "+n,function(){var e;beforeEach(function(){e=[1,2,3]}),describe("#indexOf()",function(){it("should return -1 when the value is not present",function(){assert(-1==e.indexOf(5))}),it("should return the correct index when the value is present",function(t){assert(0==e.indexOf(1)),assert(1==e.indexOf(2)),t()})})});describe("something",function(){it("should provide a useful error",function(e){setTimeout(function(){throw new Error("boom")},1)})});