var Mocha=require("../");describe("Mocha",function(){describe('"grep" option',function(){it("should add a RegExp to the mocha.options object",function(){var e=new Mocha({grep:/foo/});e.options.grep.toString().should.equal("/foo/")}),it("should convert grep string to a RegExp",function(){var e=new Mocha({grep:"foo"});e.options.grep.toString().should.equal("/foo/")})}),describe(".grep()",function(){it("should add a RegExp to the mocha.options object",function(){var e=new Mocha;e.grep(/foo/),e.options.grep.toString().should.equal("/foo/")}),it("should convert grep string to a RegExp",function(){var e=new Mocha;e.grep("foo"),e.options.grep.toString().should.equal("/foo/")}),it("should return it's parent Mocha object for chainability",function(){var e=new Mocha;e.grep().should.equal(e)})}),describe('"invert" option',function(){it("should add a Boolean to the mocha.options object",function(){var e=new Mocha({invert:!0});e.options.invert.should.be.ok})})});